let contractAddressForCDP = "0x448a5065aeBB8E423F0896E6c5D525C040f59af3";
let makerContract = web3.eth.contract(abiForMakerDAO).at(contractAddressForMakerDAO);
let CDPContract = web3.eth.contract(abiForCDP).at(contractAddressForCDP);

window.addEventListener('load', async () => {
    //check if plugin node is available if not use localhost
    // 2018 and later dapp browsers...
    if (window.ethereum) {
        window.web3 = new Web3(ethereum);
        try {
            // Request account access if needed
            await ethereum.enable();
            // Acccounts now exposed
            web3.eth.defaultAccount = web3.eth.coinbase;
        } catch (error) {
            document.getElementById("caption").textContent = 'Ethereum browser not enabled.';
        }
    }
    // pre-2018 dapp browsers...
    else if (window.web3) {
        window.web3 = new Web3(web3.currentProvider);
        // Acccounts always exposed
        web3.eth.defaultAccount = web3.eth.coinbase;
    }
    // Non-dapp browsers...
    else {
        document.getElementById("caption").textContent = 'Non-Ethereum browser detected.';
    }
    //TODO perhaps deposit is lock?
    document.getElementById("deposit").addEventListener("click", function() {

    });

    document.getElementById("withdraw").addEventListener("click", function() {

    });

    document.getElementById("payback").addEventListener("click", function() {

    });

    document.getElementById("generate").addEventListener("click", function() {

    });

    //move(address src, address dst, uint wad)
    document.getElementById("move").addEventListener("click", function() {
        let src = web3.eth.defaultAccount;
        let dst = document.getElementById("moveTo").value;
        let wad = document.getElementById("moveAmt").value;
        makerContract.move.sendTransaction(src, dist, wad, (err, data) => {
            if(err) alert(err);
            else alert(data);
        });
    });

    //TODO double check terminology
    //exit(uint256 amt)
    document.getElementById("close").addEventListener("click", function() {
        let amt = document.getElementById("closeAmt").value;
        CDPContract.exit.sendTransaction(amt, (err, data) => {
            if(err) alert(err);
            else alert(data);
        });
    });
});
